<?php$PostUrl      = 'https://sp-test.innova.com.tr/VAKIFBANK_V4/VposWeb/v3/Vposreq.aspx';$IsyeriNo     = $_POST["IsyeriNo"];$IsyeriSifre  = $_POST["IsyeriSifre"];$TerminalNo  = $_POST["TerminalNo"];$KartNo       = $_POST["KartNo"];$KartAy       = $_POST["KartAy"];$KartYil      = $_POST["KartYil"];$KartCvv      = $_POST["KartCvv"];$Tutar        = $_POST["Tutar"];$SiparID      = $_POST["SiparID"];$IslemTipi    = $_POST["IslemTipi"];$TutarKodu    = $_POST["TutarKodu"];$ClientIp     = "212.2.199.55";$PosXML = 'prmstr=<VposRequest><MerchantId>'.$IsyeriNo.'</MerchantId><Password>'.$IsyeriSifre.'</Password><TerminalNo>'.$TerminalNo.'</TerminalNo><TransactionType>'.$IslemTipi.'</TransactionType><TransactionId>'.$SiparID.'</TransactionId><CurrencyAmount>'.$Tutar.'</CurrencyAmount><CurrencyCode>'.$TutarKodu.'</CurrencyCode><Pan>'.$KartNo.'</Pan><Cvv>'.$KartCvv.'</Cvv><Expiry>'.$KartYil.$KartAy.'</Expiry><TransactionDeviceSource>0</TransactionDeviceSource><ClientIp>'.$ClientIp.'</ClientIp></VposRequest>';echo '<h1>Vpos Request</h1>';echo $PostUrl."<br>";echo '<textarea rows="15" cols="60">'.$PosXML.'</textarea>';$ch = curl_init();						   curl_setopt($ch, CURLOPT_URL,$PostUrl);curl_setopt($ch, CURLOPT_POST, 1);curl_setopt($ch, CURLOPT_POSTFIELDS,$PosXML);curl_setopt($ch, CURLOPT_RETURNTRANSFER, 5);curl_setopt($ch, CURLOPT_TIMEOUT, 59);//curl_setopt($ch, curl.options,array("CURLOPT_SSLVERSION"=>"CURL_SSLVERSION_DEFAULT"));//curl_setopt($ch, CURLOPT_SSLVERSION, 6);curl_setopt ($ch, CURLOPT_CAINFO, "../cacert.pem");//İhtiyaç olması halinde aşağıdaki proxy açabilirsiniz./*$proxy = "iproxy:8080";$proxy = explode(':', $proxy);curl_setopt($ch, CURLOPT_PROXY, $proxy[0]);curl_setopt($ch, CURLOPT_PROXYPORT, $proxy[1]);*/$result = curl_exec($ch);// Check for errors and display the error messageif($errno = curl_errno($ch)) {    //$error_message = curl_strerror($errno);    echo "cURL error ({$errno}):\n";// {$error_message}";}curl_close($ch);echo '<h1>Vpos Response</h1>';echo '<textarea rows="15" cols="60">'.$result.'</textarea>';?>